import React from 'react';

import "./style.css";

export default function ShortBio() {
    return (
        <div>
            <p>
                &nbsp;&nbsp;
                Avid cyclist and Seattle based developer with a passion for the craft of software development. I'm intent on building
                solutions that address and solve the problems of today worth solving - everything from empowering sustainability through
                technology to automating mundane, menial, or mind-numbing tasks.

                {/* I'm a natural optimist with manager experience who loves to think about
                ways to improve upon the systems that surround us and create new ones where necessary */}
            </p>

            <p>
                &nbsp;&nbsp;
                I am a tenacious learner and a quick study who accepts that there is always more to learn - and can think of few things
                more exciting than that!
            </p>

            <p>
                &nbsp;&nbsp;
                Currently looking for an opportunity to join a team that's doing awesome work so I can help them to build awesome tools!
            </p>
        </div>
    )
}
